#项目组 project_group_info，包含项目组与专家组的关联关系;#temp_project_group_infoSELECT ID,ITEM_项目组名 AS GROUP_NAME,ITEM_专家组ID AS EXPERT_GROUP_ID,ITEM_评审表FORMID AS REVIEW_FORM_ID,ITEM_综合评审表FORMID AS SUMARRY_REVIEW_FORMID,ITEM_评审时间 AS REVIEW_TIME,ITEM_高新技术领域 AS HIGH_TECH_FIELD,ITEM_指南领域 AS  SUB_HIGH_TECH_FIELD,ITEM_计划类别 AS PROJECT_TYPEFROM tlk_项目分组表 WHERE ITEM_评审时间 >= '2015-04-09'ORDER BY ITEM_评审时间 DESC;#项目和项目组关联表 project_project_group#temp_project_group_mappingSELECT a.ID,a.ITEM_项目ID AS PROJECT_ID,a.ITEM_项目组ID AS PROJECT_GROUP_ID,a.ITEM_评审表FORMID AS REVIEW_FORM_ID,a.ITEM_评审结果 AS RESULT,a.ITEM_项目序号 AS PROJECT_ORDERFROM tlk_项目分组关联表 aINNER JOIN tlk_项目分组表 b ON a.ITEM_项目组ID = b.IDWHERE ITEM_评审时间 >= '2015-04-09';#专家分组 expert_group_info#temp_expert_group_info;SELECT ID,ITEM_专家组名 AS GROUP_NAME,ITEM_评审时间 AS REVIEW_TIME,ITEM_指南领域 AS HIGH_TECH_FIELDFROM tlk_专家分组表WHERE ITEM_评审时间 >= '2015-04-09'ORDER BY ITEM_评审时间 DESC;#专家组和专家关联信息 expert_expert_group#temp_expert_group_mappingSELECT a.ITEM_专家组ID AS EXPERT_GROUP_ID,a.ITEM_专家ID AS EXPERT_ID,a.ITEM_序号 AS `ORDER`,a.ITEM_是否为财务专家 AS IS_FINANCIALFROM tlk_专家分组关联表 aINNER JOIN tlk_专家分组表 b ON a.ITEM_专家组ID = b.IDWHERE ITEM_评审时间 >= '2015-04-09';